import React from 'react'
import axios from 'axios'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Container,Button } from 'react-bootstrap'
import Table from 'react-bootstrap/Table'

const UserScreen = () => {


    const userLogin = useSelector(state=>(state.userLogin))
    const {loading,error,userInfo} =userLogin

    let present =0;
    let absent = 0;
    let leave = 0 ;
     userInfo.attendance.forEach(element => {
        if(element.presence==='P'){
            present++;
        }
        if(element.presence==='A'){
            absent++;
        }
        if(element.presence==='L'){
            leave++;
        }
    });

const [user ,setUser] = useState({});

useEffect(()=>{

const config = {
    headers:{
        'content-Type':'application/json',
        Authorization : `Bearer ${userInfo.token}`
    }
}
        const fetchUserDetails= async ()=> {
        const{data} = await axios.get('/api/users/profile',config)
        setUser(data);   
    }   
    fetchUserDetails()
})



const markPresent = (e) =>{
   
}

const markAbsent = (e) =>{
   
}


const requestLeave = (e) =>{
   
}



  return (<>
    <h1>{userInfo.name} Successfuly logged in</h1>
 
<Container>
<Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>Date</th>
          <th>presence</th>
     
        </tr>
      </thead>
      <tbody>
      {userInfo.attendance.map((obj,i)=>(
        
        <tr>
          <td>{i+1}</td>
          <td>{obj.date}</td>
          <td>{obj.presence}</td>
          
        </tr>
        
      ))}
        
      </tbody>
    </Table>
</Container>

<Container>
<Table striped bordered hover size="sm" variant="dark">
      <thead>
        <tr>
          <th>Present</th>
          <th>Absent</th>
          <th>Leaves</th>      
        </tr>
      </thead>
      <tbody>
      
        
        <tr>
          <td>{present}</td>
          <td>{absent}</td>
          <td>{leave}</td>

        </tr>
      </tbody>
    </Table>
</Container>

<Container>
<Button onClick={markPresent} className='mx-5'>mark Present</Button>
<Button onClick={markAbsent} className='mx-5' >mark Absent</Button>
<Button onClick={requestLeave} className='mx-5' >Request leave</Button>
</Container>
    </>
  )
}

export default UserScreen
