import React from 'react'
import axios from 'axios'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link,useNavigate,useParams } from 'react-router-dom'
import { Container,Button, Alert } from 'react-bootstrap'
import Table from 'react-bootstrap/Table'
import { getUserProfile } from '../action/userActions'


const AdminUserScreen = () => {

    
    const prams =useParams()
    const userr = useSelector(state=>(state.userr))
    const {loading,error,user} = userr


   
    let present = 0;
    let absent = 0;
    let leave = 0 ;
     
    


const dispatch = useDispatch()
const navigate = useNavigate();
    
useEffect(()=>{

    dispatch(getUserProfile(prams.id))
    user.attendance.forEach(element => {
      if(element.presence==='P'){
          present++;
      }
      if(element.presence==='A'){
          absent++;
      }
      if(element.presence==='L'){
          leave++;
      }
  });

})


const ChangeAttendance = (e) =>{
     const {data} = axios.put(`http://localhost:5000/api/users/${prams.id}`,{index:e.target.value})
     
}

if(!user)
return null

return (<>
    <h1>{user.name} Successfuly logged in</h1>

<Container>
<Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>Date</th>
          <th>presence</th>
     
        </tr>
      </thead>
      <tbody>
      {user.attendance.map((obj,i)=>(
        
        <tr>
          <td>{i+1}</td>
          <td>{obj.date}</td>
          <td>{obj.presence}</td>
          <td><Button value={i} onClick={ChangeAttendance} >Change</Button></td>

        </tr>
        
      ))}
        
      </tbody>
    </Table>
</Container>

<Container>
<Table striped bordered hover size="sm" variant="dark">
      <thead>
        <tr>
          <th>Present</th>
          <th>Absent</th>
          <th>Leaves</th>      
        </tr>
      </thead>
      <tbody>
      
        
        <tr>
          <td>{present}</td>
          <td>{absent}</td>
          <td>{leave}</td>

        </tr>
      </tbody>
    </Table>
</Container>
    </>
  )
}

export default AdminUserScreen
