import React from 'react'
import axios from 'axios'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link,useNavigate,useParams } from 'react-router-dom'
import { Container,Button, Alert } from 'react-bootstrap'
import Table from 'react-bootstrap/Table'
import { success,request,fail,logout,register,failRegister } from "../reducers/userReducer"

const AdminUserScreen = () => {

    
    const prams =useParams()

    const [user ,setUser] = useState({}); 
   
    let present =0;
    let absent = 0;
    let leave = 0 ;
     





    const navigate = useNavigate();

    
useEffect(()=>{
    const getdata = async () => {
    const { data } = await axios.get(`/api/users/${prams.id}`)
    setUser(data)
    user.attendance.forEach(element => {
        if(element.presence==='P'){
            present++;
        }
        if(element.presence==='A'){
            absent++;
        }
        if(element.presence==='L'){
            leave++;
        }
    });
    }
    getdata();
  
},[])


const today= new Date();
const date1 = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
const length = user.attendance.length -1






  return (<>
    <h1>{user.name} Successfuly logged in</h1>
 
<Container>
  
<Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>Date</th>
          <th>presence</th>
     
        </tr>
      </thead>
      <tbody>
      {user.attendance.map((obj,i)=>(
        
        <tr>
          <td>{i+1}</td>
          <td>{obj.date}</td>
          <td>{obj.presence}</td>
          
        </tr>
        
      ))}
        
      </tbody>
    </Table>
</Container>

<Container>
<Table striped bordered hover size="sm" variant="dark">
      <thead>
        <tr>
          <th>Present</th>
          <th>Absent</th>
          <th>Leaves</th>      
        </tr>
      </thead>
      <tbody>
      
        
        <tr>
          <td>{present}</td>
          <td>{absent}</td>
          <td>{leave}</td>

        </tr>
      </tbody>
    </Table>
</Container>


    </>
  )
}

export default AdminUserScreen
