import React from 'react'
import axios from 'axios'
import { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Container,Button } from 'react-bootstrap'
import Table from 'react-bootstrap/Table'

const UserScreen = () => {

    
    const userLogin = useSelector(state=>(state.userLogin))
    const {loading,error,userInfo} =userLogin
    
    let present =0;
    let absent = 0;
    let leave = 0 ;
     userInfo.attendance.map(element => {
        if(element.presence==='P'){
            present++;
        }
        if(element.presence==='A'){
            absent++;
        }
        if(element.presence==='L'){
            leave++;
        }
    });







    const [user ,setUser] = useState(""); 
useEffect(()=>{

   setUser(userInfo);

},[])


const markPresent = async (e)=>{
    
    const {data} = await axios.put('/api/users/profile', {_id:user._id,presence:'P'})
     setUser(data)
}

const markAbsent = async (e) => {
    const {data} = await axios.put('/api/users/profile', {_id:user._id,presence:'A'})
    setUser(data)
}


  return (<>
    <h1>{user.name} Successfuly logged in</h1>
 
<Container>
<Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>Date</th>
          <th>presence</th>
     
        </tr>
      </thead>
      <tbody>
      {user.attendance.map((obj,i)=>(
        
        <tr>
          <td>{i+1}</td>
          <td>{obj.date}</td>
          <td>{obj.presence}</td>
          
        </tr>
        
      ))}
        
      </tbody>
    </Table>
</Container>

<Container>
<Table striped bordered hover size="sm" variant="dark">
      <thead>
        <tr>
          <th>Present</th>
          <th>Absent</th>
          <th>Leaves</th>      
        </tr>
      </thead>
      <tbody>
      
        
        <tr>
          <td>{present}</td>
          <td>{absent}</td>
          <td>{leave}</td>

        </tr>
      </tbody>
    </Table>
</Container>

<Container>
<Button onClick={markPresent} className='mx-5'>mark Present</Button>
<Button onClick={markAbsent} className='mx-5' >mark Absent</Button>
<Button className='mx-5' >Request leave</Button>
</Container>
    </>
  )
}

export default UserScreen
